public class Switch extends Field {
	
	private Pit pit;
	
	public void SetPit(Pit p) {
		pit = p;
	}
	
	public Pit GetPit() {
		return pit;
	}
	
	public boolean HitBy(Direction dir, Worker w) {
		if(GetThing() != null)
			if(GetThing().HitBy(dir, w)) {
					pit.SetOpen(false);;
					return true;
			}
			else
				return false;
		else
			return true;	
	}
	
	public boolean HitBy(Direction dir, Box b) {
		if(GetThing() != null)
			if(GetThing().HitBy(dir, b)) {
					pit.SetOpen(true);;
					return true;
			}
			else
				return false;
		else
			return true;
	}
}
